mapper.select:
SELECT count(student_id) FROM STUDENT WHERE 1=1
AND school_type = #{gubun}
AND student_name LIKE CONCAT (#{studentName}, '%')

SELECT * FROM STUDENT
WHERE 1=1

	AND school_type = #{gubun}


	AND student_name LIKE CONCAT (#{studentName}, '%')

LIMIT #{start}, #{end}

SELECT count(student_memo_id)
FROM STUDENT_MEMO
WHERE student_id = #{studentId}

SELECT A.*, B.member_name
FROM STUDENT_MEMO A LEFT JOIN FLOWEDU_MEMBER B
ON A.flow_member_id = B.flow_member_id
WHERE student_id = #{studentId}
LIMIT #{start}, #{end}

mapper.update:
UPDATE STUDENT
student_name = #{studentName}, student_password = #{studentPassword}, student_gender = #{studentGender}, student_birthday = #{studentBirthday}, home_tel_number = #{homeTelNumber}, student_phone_number = #{studentPhoneNumber}, student_email = #{studentEmail}, school_name = #{schoolName}, school_type = #{schoolType}, student_grade = #{studentGrade}, student_photo_file = #{studentPhotoFile}, student_photo_url = #{studentPhotoUrl}, student_memo = #{studentMemo}, mother_name = #{motherName}, mother_phone_number = #{motherPhoneNumber}, father_name = #{fatherName}, father_phone_number = #{fatherPhoneNumber},
student_id = #{studentId}
